section.source:
using Apache.Avalon.Castle.ManagementExtensions;

[ManagedComponent]
public class DummyHttpServer
{
	protected bool started = false;

	public DummyHttpServer()
	{
	}

	[ManagedAttribute]
	public bool Started
	{
		get
		{
			return started;
		}
		set
		{
			started = value;
		}
	}

	[ManagedOperation]
	public void Start()
	{
		Started = true;
	}

	[ManagedOperation]
	public void StartAt(int time)
	{
	}

	[ManagedOperation]
	public void Stop()
	{
		Started = false;
	}
}

[ManagedComponent]
public class DummyLifecycledService : MRegistrationListener
{
	public DummyLifecycledService()
	{
	}

	[ManagedOperation]
	public void Start()
	{
	}

	[ManagedOperation]
	public void Stop()
	{
	}

	public void BeforeRegister(MServer server, ManagedObjectName name)
	{
	}

	public void AfterDeregister()
	{
	}

	public void AfterRegister()
	{
	}

	public void BeforeDeregister()
	{
	}
}
